sections.code:
.watch-main-col,
ytd-video-primary-info-renderer > div:first-of-type /* Magic Actions extension toolbar */ {
	display: none;
}

.Trends.module,
.wtf-module,
.DismissableModule,
.DismissibleModule,
li.moments,
.ScrollBump,
.Footer-adsModule,
#larry-active,
.LiveVideoHomePageModuleContainer,
li.stream-item[data-suggestion-json *= "ActivityTweet"],
li[data-component-context = "generic_activity"] {
	display: none !important;
}
.Footer > .flex-module {
	border-radius: 5px !important;
}

.avatar,
.avatar--circular,
.DMAvatar,
#user-dropdown-toggle,
#user-dropdown-toggle .Avatar,
.ProfileCard-avatarImage {
	border-radius: 3px;
	image-rendering: -webkit-optimize-contrast;
}

.DashboardProfileCard-avatarImage {
	border-radius: 5px;
}

.ProfileCard-avatarLink {
	border-radius: 6px;
}

.DashboardProfileCard-avatarLink {
	background-color: transparent;
}

.ProfileAvatar-image {
	border-radius: 7px;
}
.ProfileAvatar {
	border-radius: 12px;
}

.ambox,
#mw-head,
#mw-head-base,
#mw-page-base,
#mw-panel,
.articleFeedback,
#catlinks,
#footer,
#localNotice,
.stub,
#siteSub,
.editsection,
.articleFeedbackv5-bottomrighttab {
	display: none !important;
}

#content {
	margin-left: 0 !important;
}

body, #content {
	background: white !important;
}

body.question-page #sidebar,
body.question-page #footer,
body.question-page .bottom-notice,
body.question-page #header,
body.question-page header,
body.question-page .topbar,
body.question-page .question-status,
body.question-page .fw,
body.question-page .comments-link,
body.question-page #tabs,
body.question-page .post-taglist,
body.question-page .comment-date,
body.question-page .comment-user,
body.question-page .bounty-notification,
body.question-page #post-form,
body.question-page #feed-link,
body.question-page .aside-cta,
body.question-page .js-link-separator {
    display: none !important;
}

body.question-page #mainbar {
    float: none !important;
    width: auto !important;
}

body.question-page #question-header {
    width: auto !important;
}

body.question-page #content {
    width: 728px !important;
    padding-top: 17px !important;
}

body.question-page .comment-body {
    visibility: collapse !important;
}

body.question-page .comment-copy {
    visibility: visible !important;
}

body.question-page .so-header ~ .container {
    padding-top: 0 !important;
}

body.question-page .top-bar~.container {
    margin-top: 0 !important;
}

/* Make emoji (and their image replacements) four times as large */
.message_body .emoji-sizer {
    font-size: 2.75rem !important;
    line-height: 2.25rem !important;
}
.emoji-only.emoji-sizer {
    font-size: 4rem !important;
    line-height: 4rem !important;
}

html {
	overflow-y: hidden;
}

nav {
    visibility: collapse;
}

.nav .dropdown {
    visibility: visible;
}

.main-container {
    padding-top: 11px;
}

.content-cell {
    top: 25px !important;
}

#sidebar-shared,
#sidebar-bookmarked,
#sidebar-trending,
.btns .btn-group,
.btn-share,
.add-icon,
.btns .likes {
    display: none !important;
}

.floating {
    position: fixed;
    right: 66px;
    top: 9px;
}

.content-cell .slide {
    top: 33px !important;
}

.post {
	padding: 0;
}

.post .header .name {
    margin-top: 1px !important;
}

.post .header .list-header {
 	width: auto !important;
    float: none !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    position: absolute !important;
    left: 139px !important;
    right: 164px !important;
    direction: ltr !important;
}

.static .pull-right {
	margin-left: 4px;
}

.post .header .label.pull-right {
    max-width: none !important;
}

.label {
    text-align: left !important;
}

.list-post * {
    direction: ltr !important;
}

div[data-list-toggle] {
    padding: 5px 10px 0 5px;
    box-sizing: content-box;
    cursor: pointer;
}

div.list-post {
    padding: 0 5px 5px;
}
