sections.code:
.watch-main-col,
#related, /* Related videos sidebar. If you try to hide this using Magic Actions, the video element is rendered too small and has a white border to the right and bottom. */
ytd-video-primary-info-renderer > div, /* Magic Actions extension toolbar */
ytd-video-primary-info-renderer > span { /* Magic Actions extension toolbar again */
	display: none;
}

ytd-video-primary-info-renderer > .ytd-video-primary-info-renderer {
	display: block;
}

/* hide recommended videos after the main video ends */
.ytp-endscreen-content {
	display: none;
}

/* Hide shit */

.Trends.module,
.wtf-module,
.DismissableModule,
.DismissibleModule,
li.moments,
.ScrollBump,
.Footer-adsModule,
#larry-active,
.LiveVideoHomePageModuleContainer,
li.stream-item[data-suggestion-json *= "ActivityTweet"],
li[data-component-context = "generic_activity"],
li.people .count,
.Footer {
	display: none !important;
}

/* Rounded rectangle profile pictures */

.avatar,
.avatar--circular,
.DMAvatar,
#user-dropdown-toggle,
#user-dropdown-toggle .Avatar {
	border-radius: 3px;
	image-rendering: optimizeQuality;
	image-rendering: -webkit-optimize-contrast;
}

.DashboardProfileCard-avatarImage,
.ProfileCard-avatarImage {
	border-radius: 5px;
}

.ProfileCard-avatarLink {
	border-radius: 6px;
}

.DashboardProfileCard-avatarLink {
	background-color: transparent;
}

.ProfileAvatar-image {
	border-radius: 7px;
}
.ProfileAvatar {
	border-radius: 12px;
}

/* Profile box is missing top border for some reason */

.DashboardProfileCard-bg {
    margin-top: 0px;
}

.ambox,
#mw-head,
#mw-head-base,
#mw-page-base,
#mw-panel,
.articleFeedback,
#catlinks,
#footer,
#localNotice,
.stub,
#siteSub,
.editsection,
.articleFeedbackv5-bottomrighttab {
	display: none !important;
}

#content {
	margin-left: 0 !important;
}

body, #content {
	background: white !important;
}

body.question-page #sidebar,
body.question-page #footer,
body.question-page .bottom-notice,
body.question-page #header,
body.question-page header,
body.question-page .topbar,
body.question-page .question-status,
body.question-page .fw,
body.question-page .comments-link,
body.question-page #tabs,
body.question-page .post-taglist,
body.question-page .comment-date,
body.question-page .comment-user,
body.question-page .bounty-notification,
body.question-page #post-form,
body.question-page #feed-link,
body.question-page .aside-cta,
body.question-page .js-link-separator,
body.question-page #left-sidebar {
    display: none !important;
}

body.question-page #mainbar {
    float: none !important;
    width: auto !important;
}

body.question-page #question-header {
    width: auto !important;
}

body.question-page #content {
    width: 728px !important;
    padding-top: 17px !important;
}

body.question-page .comment-body {
    visibility: collapse !important;
}

body.question-page .comment-copy {
    visibility: visible !important;
}

body.question-page .so-header ~ .container {
    padding-top: 0 !important;
}

body.question-page .top-bar~.container {
    margin-top: 0 !important;
}

/* Make emoji (and their image replacements) four times as large */
.message_body .emoji-sizer {
    font-size: 2.75rem !important;
    line-height: 2.25rem !important;
}
.emoji-only.emoji-sizer {
    font-size: 4rem !important;
    line-height: 4rem !important;
}

#below_tree {
	display: none;
}

.article_sub_title .icon-service_globe-16x16,
.article_sub_title .icon-coffee_empty {
	display: none;
}

.article_footer_buttons.smallradius[title*='Send article to external services'],
.article_footer_buttons.smallradius[title*='Share by email']{
    display: none;
}

#sb_rp_section_options {
    display: none;
}

#sb_rp_notifications {
    display: none;
}
#sb_rp_tools {
    right: 40px;
}

#sb_rp_prev_article,
#sb_rp_next_article,
.inno_toolbar_spacer {
    display: none;
}

#sb_rp_gear {
    right: 0;
}

.article_content video {
    border: 4px inset #999;
}

.ar[data-tags *= '"Play"'] .column_view_thumbnail {
    opacity: 0;
}
.ar[data-tags *= '"Play"']:hover .column_view_thumbnail {
    opacity: 1;  
}

/* Make two-column layout scale to viewport width correctly */
#ad {
    display: none;
}

/* Allow HTML5 video context menu, useful for Video Popout */
.ima-ads-container {
    display: none;
}
