sections.code:
.watch-main-col,
#related, /* Related videos sidebar */
#logo, /* Site logo */
#search, /* Search box */
#end, /* Account buttons in the top right */
.ytp-endscreen-content, /* Recommended videos after the main video ends */
#comments, /* Comments section below the video */
#info, /* View count, ratings, share, and save buttons */
#subscribe-button, /* Subscribe and notify buttons */
.ytp-pause-overlay /* More Videos bar that appears when you pause */
/* #more */ /* Progressive disclosure button for description, which is always expanded */
{
	display: none !important;
}

ytd-video-secondary-info-renderer {
    border-bottom: none;
}

ytd-expander[collapsed] > #content.ytd-expander {
/*     overflow: visible; */
}

/* Hide shit */
.Trends.module,
.wtf-module,
.DismissableModule,
.DismissibleModule,
li.moments,
.ScrollBump,
.Footer-adsModule,
#larry-active,
.LiveVideoHomePageModuleContainer,
li.stream-item[data-suggestion-json *="ActivityTweet"],
li[data-component-context="generic_activity"],
li.people .count,
.Footer,
li[data-tweet-hidden-parent='1'],
.self-thread-head,
h1.Icon {
    display: none !important;
}

/* Rounded rectangle profile pictures */
.avatar,
.avatar--circular,
.DMAvatar,
#user-dropdown-toggle,
#user-dropdown-toggle .Avatar {
    border-radius: 3px;
    image-rendering: optimizeQuality;
    image-rendering: -webkit-optimize-contrast;
}

.DashboardProfileCard-avatarImage,
.ProfileCard-avatarImage {
    border-radius: 5px;
}

.ProfileCard-avatarLink {
    border-radius: 6px;
}

.DashboardProfileCard-avatarLink {
    background-color: transparent;
}

.ProfileAvatar-image {
    border-radius: 7px;
}
.ProfileAvatar {
    border-radius: 12px;
}

/* Profile box is missing top border for some reason */
.DashboardProfileCard-bg {
    margin-top: 0px;
}

.content-main {
    border-radius: 3px;
}
.DashboardProfileCard {
    border-radius: 3px;
}

/* Show absolute timestamps */
.tweet-timestamp::after {
    content: attr(title) attr(data-original-title);
}
.tweet-timestamp ._timestamp {
    display: none;
}

/* Hide reply, retweet, like, and direct message buttons below tweets that aren't mine */
.tweet.js-stream-tweet:not(.my-tweet) .ProfileTweet-actionList {
    display: none;
}
.stream-item .content {
    min-height: 49px;
}

.ambox,
#mw-head,
#mw-head-base,
#mw-page-base,
#mw-panel,
.articleFeedback,
#catlinks,
#footer,
#localNotice,
.stub,
#siteSub,
.editsection,
.articleFeedbackv5-bottomrighttab {
	display: none !important;
}

#content {
	margin-left: 0 !important;
}

body, #content {
	background: white !important;
}

body.question-page #sidebar,
body.question-page #footer,
body.question-page .bottom-notice,
body.question-page #header,
body.question-page header,
body.question-page .topbar,
body.question-page .question-status,
body.question-page .fw,
body.question-page .comments-link,
body.question-page #tabs,
body.question-page .post-taglist,
body.question-page .comment-date,
body.question-page .comment-user,
body.question-page .bounty-notification,
body.question-page #post-form,
body.question-page #feed-link,
body.question-page .aside-cta,
body.question-page .js-link-separator,
body.question-page #left-sidebar {
    display: none !important;
}

body.question-page #mainbar {
    float: none !important;
    width: auto !important;
}

body.question-page #question-header {
    width: auto !important;
}

body.question-page #content {
    width: 728px !important;
    padding-top: 17px !important;
}

body.question-page .comment-body {
    visibility: collapse !important;
}

body.question-page .comment-copy {
    visibility: visible !important;
}

body.question-page .so-header ~ .container {
    padding-top: 0 !important;
}

body.question-page .top-bar~.container {
    margin-top: 0 !important;
}

/* Make emoji (and their image replacements) four times as large */
.message_body .emoji-sizer {
    font-size: 2.75rem !important;
    line-height: 2.25rem !important;
}
.emoji-only.emoji-sizer {
    font-size: 4rem !important;
    line-height: 4rem !important;
}

#below_tree {
	display: none;
}

.article_sub_title .icon-service_globe-16x16,
.article_sub_title .icon-coffee_empty {
	display: none;
}

.article_footer_buttons.smallradius[title*='Send article to external services'],
.article_footer_buttons.smallradius[title*='Share by email']{
    display: none;
}

#sb_rp_section_options {
    display: none;
}

#sb_rp_notifications {
    display: none;
}
#sb_rp_tools {
    right: 40px;
}

#sb_rp_prev_article,
#sb_rp_next_article,
.inno_toolbar_spacer {
    display: none;
}

#sb_rp_gear {
    right: 0;
}

.article_content {
    max-width: unset;
}

.article_content video {
    max-width: 100%;
    height: auto;
}

.ar[data-tags *= '"Play"'] .column_view_thumbnail {
    opacity: 0;
}
.ar[data-tags *= '"Play"']:hover .column_view_thumbnail {
    opacity: 1;  
}

#sb_tp_search_overlay, #sb_tp_margin {
    display: none;
}

/* Get rid of unclickable margins between articles in Column View */
.reader_pane_view_style_2 .article_header_text {
    padding-top: 10px;
    padding-bottom: 7px;
}
.reader_pane_view_style_2 .article_header_pane_left {
    padding-top: 10px;
}
.reader_pane_view_style_2 .article_header {
    top: 0 !important;
}
.reader_pane_view_style_2 .ar {
    padding-top: 0;
    padding-bottom: 0;
}

/* Make two-column layout scale to viewport width correctly */
#ad {
    display: none;
}

/* Allow HTML5 video context menu, useful for Video Popout */
.ima-ads-container,
.player-overlay,
.extension-container {
    display: none;
}
